mapper.select:
SELECT 

FROM oa_indoc a

WHERE a.indocid = #{indocid}

SELECT 

FROM oa_indoc a


	a.indoccreator = #{currentUser.loginName}

		AND a.indocname LIKE 
			'%'||#{indocname}||'%'
			'%'+#{indocname}+'%'
			concat('%',#{indocname},'%')


		AND a.indocnextop = #{indocnextop}




		ORDER BY ${page.orderBy}

SELECT 

FROM oa_indoc a






		ORDER BY ${page.orderBy}
